	UPDATE dbo.Assignments
	SET EmployeeId=IsNull(NullIf(@employeeId, ''), EmployeeId), StartDate=IsNull(NullIf(@startDate, ''), StartDate), EndDate=IsNull(NullIf(@endDate, ''), EndDate)
	WHERE Id = @id;

end

